package pravinSDec19;

public class checkAsciiValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (char i = 'A'; i <= 'Z'; i++) {

			System.out.print((int) i + " ");

		}

		System.out.println();

		for (char i = 'a'; i <= 'z'; i++) {

			System.out.print((int) i + " ");

		}

	}
}